// EditProfile.js
import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const EditProfile = ({ jwtToken, user }) => {
  const [bio, setBio] = useState(user.bio || '');
  const [picture, setPicture] = useState(null);
  const handleBioChange = (e) => {
    setBio(e.target.value);
  };

  const handlePictureChange = (e) => {
    setPicture(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const response = await fetch(`http://localhost:5000/users/edit?username=${user.username}`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${jwtToken}`,
        },
        body: JSON.stringify({ bio })
      });
  
      if (response.ok) {
        console.log('Profile updated successfully');
      } else {
        console.error('Profile update failed');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div>
      <h2>Edit Profile</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="bio">
          <Form.Label>Bio</Form.Label>
          <Form.Control
            type="text"
            rows={3}
            value={bio}
            onChange={(e) => setBio(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="picture">
          <Form.Label>Profile Picture</Form.Label>
          <Form.Control type="file" onChange={handlePictureChange} />
        </Form.Group>
        <Button variant="primary" type="submit">
          Save
        </Button>

        <Link to="/mainpage" className="btn btn-secondary ml-2">
          Back to Main Page
        </Link>

      </Form>
  
    </div>
  );
};

export default EditProfile;
