import React, { useState, useEffect } from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const UserCard = ({ user, handleLike, onDislike }) => {
  const [showPopup, setShowPopup] = useState(false);
  const [liked, setLiked] = useState(false);

  const handleClick = async () => {
    await handleLike();
  };

  const handleProfileClick = () => {
    setShowPopup(true);
  };

  const handleClose = () => {
    setShowProfileModal(false);
  };

  return (
    <>
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={user.picture} />
      <Card.Body>
        <Card.Title>
            <Button variant="link" className="text-primary text-decoration-none" onClick={handleProfileClick}>
              <h1>{user.name}</h1>
            </Button>
        </Card.Title>
        <Card.Text>{user.bio}</Card.Text>
        <Button variant="primary" onClick={handleClick}>
          Like
        </Button>
        <Button variant="danger" onClick={handleClick} className="ml-2">
          Dislike
        </Button>
      </Card.Body>
    </Card>

    <Modal show={showProfileModal} onHide={handleClose}>
    <Modal.Header closeButton>
      <Modal.Title>{user.name}'s Profile</Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <p><strong>Bio:</strong> {user.bio}</p>
      <p><strong>Picture:</strong></p>
      <img src={user.picture} alt="User Profile" style={{ maxWidth: '100%', height: 'auto' }} />
    </Modal.Body>
    </Modal>
    </>

  );
};

export default UserCard;
