import React from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const Menu = ({ setJwtToken, setViewOption }) => {

  const navigate = useNavigate();
  // Function to handle logout
  const handleLogout = () => {
    setJwtToken(null);
    navigate("/");
  };

  // Function to toggle edit profile view
  const handleToggleEditProfile = () => {
    setViewOption('editprofile');
  };

  // Function to toggle edit profile view
  const handleToggleChats = () => {
    setViewOption('chats');
  };

  return (
    <div className="fixed-bottom d-flex justify-content-center">
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <div className="card">
              <ul className="list-group list-group-horizontal list-unstyled d-flex justify-content-around">
                <li className="list-group-item">
                  <button className="btn btn-primary" onClick={handleToggleEditProfile}>
                    Edit Profile
                  </button>
                </li>
                <li className="list-group-item">
                  <button className="btn btn-primary" onClick={handleToggleChats}>
                    View Chats
                  </button>
                </li>
                <li className="list-group-item">
                  <button className="btn btn-primary" onClick={handleLogout}>
                    Logout
                  </button>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Menu;
