import React, { useState } from 'react';
import { Card, Button, Modal, Row, Col } from 'react-bootstrap';

const UserCard = ({ user, handleLike, handleDislike }) => {
  const [showPopup, setShowPopup] = useState(false);

  const handleClick = async () => {
    await handleLike();
  };

  const handleDislikeClick = async () => {
    await handleDislike();
  };

  const handleProfileClick = () => {
    setShowPopup(true);
  };

  const handleClose = () => {
    setShowPopup(false);
  };

  return (
    <>
    <Card style={{ width: '18rem' }}>
      <Card.Body>
        <Row>
          <Col>
            <Button variant="link" className="text-primary text-decoration-none" onClick={handleProfileClick}>
              <h1>{user.name}</h1>
            </Button>
          </Col>
          <Col xs="auto">
            <Button variant="primary" onClick={handleClick}>
              Like
            </Button>
          </Col>
          <Col xs="auto">
            <Button variant="danger" onClick={handleDislikeClick} className="ml-2">
              Dislike
            </Button>
          </Col>
        </Row>
        <Card.Text>{user.bio}</Card.Text>
      </Card.Body>
    </Card>

    <Modal show={showPopup} onHide={handleClose}>
    <Modal.Header closeButton>
      <Modal.Title>{user.name}'s Profile</Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <p><strong>Bio:</strong> {user.bio}</p>
      <p><strong>Register Date: {user.registerDate}</strong></p>
    </Modal.Body>
    </Modal>
    </>

  );
};

export default UserCard;
