import React, { useState } from 'react';
import Chat from '../components/Chat';
import ChatList from '../components/ChatList';
import { Button } from 'react-bootstrap';


const Chats = ({setViewOption, user, jwtToken}) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await fetch('', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${jwtToken}`,
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch messages');
        }

        const data = await response.json();
        setMessages(data);
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    fetchMessages();
  }, [jwtToken]);
  
  
  // Function to toggle edit profile view
  const handleToggleEditProfile = () => {
    setViewOption('mainpage');
  };


  return (
    <div className='chatpage'>
        <ChatList user={user} jwtToken={jwtToken}/>
        <Chat />
        <Button className="btn btn-secondary ml-2" onClick={handleToggleEditProfile}>
          Back to Main Page
        </Button>
    </div>
  );
}; 

export default Chats;
