import React, { useState, useEffect } from 'react';
import UserCard from '../components/UserCard';
import Menu from '../components/Menu';
import { useNavigate } from 'react-router-dom';
import './MainPage.css'

const MainPage = ({ jwtToken, user, setUser, setJwtToken }) => {
  const [users, setUsers] = useState([]); // Fetch and set user data
  const [currentUser, setCurrentUser] = useState(null); // Fetch and set current user data
  const [showPopup, setShowPopup] = useState(false); // State to toggle popup visibility
  const navigate = useNavigate();

  // Fetch users and current user data on component mount
  useEffect(() => {
    // Check if jwtToken exists, if not redirect to login page
    if (!jwtToken) {
      navigate('/login');
      return;
    }

    // Fetch user data based on username
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/users?username=${user.username}`, {
          headers: {
            Authorization: `Bearer ${jwtToken}`,
          },
        });
        if (response.ok) {
          const userData = await response.json();
          setUser(userData)
        } else {
          console.error('Failed to fetch user data');
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [jwtToken, user.username, navigate]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch(`http://localhost:5000/users/all?username=${user.username}`, {
          headers: {
            Authorization: `Bearer ${jwtToken}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setUsers(data);
          setCurrentUser(data[0]);
        } else {
          console.error('Error fetching users:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, [jwtToken, user.username]);

  const handleLike = async () => {
    try {
      const response = await fetch(`http://localhost:5000/users/${currentUser._id}/like`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${jwtToken}`,
        },
      });
  
      if (response.ok) {
        const updatedUser = await response.json();
        setCurrentUser(updatedUser);
        // Add the current user to the likes field of the user being liked
        const likedUser = users.find((user) => user._id.toString() === updatedUser._id);
        if (likedUser) {
          likedUser.likes.push(user._id);
          setUsers([...users]);
        }
      } else {
        console.error('Error liking user:', response.statusText);
      }
    } catch (error) {
      console.error('Error liking user:', error);
    }
  };

  // Function to handle username click and toggle popup visibility
  const handleUsernameClick = () => {
    setShowPopup(!showPopup);
  };

  return (
    <>
      <h1 onClick={handleUsernameClick}>{user.username}</h1>
      {showPopup && (
        <div className="popup show">
          <h2>{user.name}</h2>
          <p>Bio: {user.bio}</p>
          <p>Register date: {user.registerDate}</p>
          <button onClick={handleUsernameClick}>Close</button>
        </div>
      )}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        {currentUser && <UserCard user={currentUser} handleLike={handleLike}/>}
      </div>
      <Menu setJwtToken={ setJwtToken }/>
    </>
  );
};

export default MainPage;
