import React from 'react';
import { useNavigate } from 'react-router-dom';

const Menu = ({ setJwtToken, setViewOption }) => {
  const navigate = useNavigate();

  // Function to handle logout
  const handleLogout = () => {
    setJwtToken(null);
    navigate('/');
  };

  // Function to toggle edit profile view
  const handleToggleEditProfile = () => {
    setViewOption('editprofile');
  };

  // Function to toggle edit profile view
  const handleToggleChats = () => {
    setViewOption('chats');
  };

  return (
    <div className="fixed-bottom">
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-4 d-flex justify-content-center">
            <button className="btn btn-primary" onClick={handleToggleEditProfile}>
              Edit Profile
            </button>
          </div>
          <div className="col-md-4 d-flex justify-content-center">
            <button className="btn btn-primary" onClick={handleToggleChats}>
              View Chats
            </button>
          </div>
          <div className="col-md-4 d-flex justify-content-center">
            <button className="btn btn-primary" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Menu;
