import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const UserCard = ({ user, handleLike, onDislike }) => {

  const [liked, setLiked] = useState(false);

  const handleClick = async () => {
    await handleLike();
    setLiked(true);
  };


  return (
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={user.picture} />
      <Card.Body>
        <Card.Title>
          <Link to={`/profile/${user.username}`} className="text-primary text-decoration-none">
            <h1>{user.name}</h1>
          </Link>
        </Card.Title>
        <Card.Text>{user.bio}</Card.Text>
        <button onClick={handleClick}>{liked ? 'Liked' : 'Like'}</button>
        <Button variant="danger" onClick={onDislike} className="ml-2">
          Dislike
        </Button>
      </Card.Body>
    </Card>
  );
};

export default UserCard;
